mapper.select:
SELECT ROWNUM, COUNT(pr.PROJECT_NUMBER) cont, pr.PROJECT_NUMBER, pr.TITLE, pr.CATEGORY, pr.CONTENT, pr.AUTHENTICATION, pr.START_DATE, pr.END_DATE, pr.STATUS FROM ( SELECT ROWNUM, COUNT(pr.PROJECT_NUMBER) cont, pr.PROJECT_NUMBER, pr.TITLE, pr.CATEGORY, pr.CONTENT, pr.AUTHENTICATION, pr.START_DATE, pr.END_DATE, pr.STATUS FROM TBL_PROJECT pr,TBL_PARTICIPATION pa WHERE pr.PROJECT_NUMBER = pa.PROJECT_NUMBER
AND pr.CATEGORY = '예술'
AND pr.STATUS = 1 <!--인증된 목록--> AND ROWNUM <= #{criteria.pageNum} * #{criteria.amount} GROUP BY ROWNUM, pr.PROJECT_NUMBER, pr.TITLE, pr.CATEGORY, pr.CONTENT, pr.AUTHENTICATION, pr.START_DATE, pr.END_DATE, pr.STATUS, pa.PROJECT_NUMBER, ROWNUM;
ORDER BY cont DESC;인기순
ORDER BY ROWNUM DESC; 신규
) WHERE RN > (#{criteria.pageNum} - 1) * #{criteria.amount}

SELECT RN, PJ.PROJECT_NUMBER, PJ.TITLE, PJ.CATEGORY, PJ.CONTENT, PJ.AUTHENTICATION, PJ.START_DATE, PJ.END_DATE FROM ( SELECT ROWNUM RN, PJ.PROJECT_NUMBER, PJ.TITLE, PJ.CATEGORY, PJ.CONTENT, PJ.AUTHENTICATION, PJ.START_DATE, PJ.END_DATE FROM TBL_PROJECT PJ, TBL_PARTICIPATION PT
WHERE ROWNUM <= #{criteria.pageNum} * #{criteria.amount} ORDER BY COUNT(PT.USER_NUMBER)
WHERE ROWNUM <= #{criteria.pageNum} * #{criteria.amount} ORDER BY PJ.START_DATE DESC
WHERE PJ.CATEGORY =#{category} AND ROWNUM <= #{criteria.pageNum} * #{criteria.amount} ORDER BY PJ.START_DATE DESC
) WHERE RN > (#{criteria.pageNum} - 1) * #{criteria.amount}
