mapper.select:
SELECT APPLY_COUNT, PROJECT_NUMBER, TITLE, CATEGORY, CONTENT, AUTHENTICATION, START_DATE, END_DATE, STATUS FROM ( SELECT APPLY_COUNT, ROWNUM, PROJECT_NUMBER, TITLE, CATEGORY, CONTENT, AUTHENTICATION, START_DATE, END_DATE, STATUS FROM ( SELECT COUNT(pr.PROJECT_NUMBER) APPLY_COUNT, pr.PROJECT_NUMBER, pr.TITLE, pr.CATEGORY, pr.CONTENT, pr.AUTHENTICATION, pr.START_DATE, pr.END_DATE, pr.STATUS FROM TBL_PROJECT pr JOIN TBL_PARTICIPATION pa ON pr.PROJECT_NUMBER = pa.PROJECT_NUMBER WHERE pr.STATUS = 1
AND pr.CATEGORY = #{category}
GROUP BY pr.PROJECT_NUMBER, pr.TITLE, pr.CATEGORY, pr.CONTENT, pr.AUTHENTICATION, pr.START_DATE, pr.END_DATE, pr.STATUS, pa.PROJECT_NUMBER ) WHERE ROWNUM <= #{criteria.pageNum} * #{criteria.amount}
ORDER BY APPLY_COUNT DESC
ORDER BY ROWNUM DESC
) WHERE ROWNUM > (#{criteria.pageNum} - 1) * #{criteria.amount}
