mapper.select:

SELECT CONTENT , RGISTER_DATE FROM
    (
        SELECT /*+ INDEX(TBL_REPLY SYS_C007522) */ CONTENT , RGISTER_DATE, ROWNUM RN
        FROM TBL_REPLY
        WHERE REVIEW_NUMBER = #{reviewNumber} AND ROWNUM <= #{criteria.pageNum} * #{criteria.amount}
    )
WHERE RN > (#{criteria.pageNum} - 1) * #{criteria.amount}
