mapper.update:
UPDATE TBL_USER
SET NAME = #{name}, NICKNAME = #{nickname},
    CONTENT = #{content}, EMAIL = #{email}
WHERE USER_NUMBER = #{userNumber}

UPDATE TBL_USER
SET PASSWORD = #{newPassword}
WHERE PASSWORD = #{userVO.password} AND USER_NUMBER = #{userVO.userNumber}

UPDATE TBL_USER
SET ACHIEVEMENT_NUMBER = #{achievementNumber}
WHERE USER_NUMBER = #{userNumber}

mapper.select:
SELECT USER_NUMBER, EMAIL, NAME, NICKNAME, CONTENT, KAKAO, REGISTER_DATE FROM TBL_USER WHERE ${type} LIKE '%'||#{keyword}||'%'
KAKAO IS NOT NULL
KAKAO IS NULL
REGISTER_DATE >= #{startDate}
REGISTER_DATE <= #{endDate}
